import React from "react";
import styled from "styled-components";

const IntroductionStyled = styled.div`
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  margin: auto;

  h1{
  margin: 20px;
  }
  p{
    line-height: 1.7;
  }
`

export default IntroductionStyled
