import React, {useRef, useState} from "react";

const ContactForm = () => {
    const [formSent, setFormSent] = useState(false)
    const [formContent, setFormContent] = useState({
        email:"",
        name:"",
        message:"",
    })

    const inputChange = (e) => {
        setFormContent({
            ...formContent,
            [e.target.name]: e.target.value
        })
    }

    const handleSubmit = (e :any) =>{
        const formData = new FormData();
        formData.set('email', formContent.email);
        // Set needed hardcoded values for Listrak form
            formData.set('CheckBox.SourceDrop Event', 'on');
        formData.set(
                'crvs',
                'w7xmvNEUZhabLaCR7yp-g1SFNLHaCQop_qnebKl2ITf-bDdnGCEngzplrh1FAVtChDP4arqnuOQ0zTcVkesXPQHnvAMeGTYFHwdVzF-WzGtE5s4A1J3KyWSyV-ac7XBq'
            );
        formData.set('ABC', '');
        formData.set('XYZ', '');
        formData.set('AtZ', '');
        formData.set('RadioButton.Source', 'off');

        fetch('https://formsubmit.co/0d4a65b9d8079e2eef27da8b90e1bb79',
            {method: 'POST', body: formData})
            .then(response => setFormSent(true))
            .catch(error => {
                    setErrorMessage(true);
                });


    }

    return <div className="content">
        <div className="section contact-form-section">
            <div className="form-title">
                <h1 className="has-text-centered">Obrigado por retirar um tempo para me contactar. <br/>
                    Como posso te ajudar hoje?
                </h1>
            </div>
            <form id="contact-form" action="https://formsubmit.co/0d4a65b9d8079e2eef27da8b90e1bb79" method="POST">
                <div className="columns is-centered">
                    <div className="column">
                        <div className="field">
                            <label className="label">Username</label>
                            <div className="control">
                                <input className="input" type="text" placeholder="Text input" value={formContent.name} onChange={inputChange}/>
                            </div>
                        </div>
                    </div>
                    <div className="column">
                        <div className="field">
                            <label className="label">Username</label>
                            <div className="control">
                                <input className="input" type="text" placeholder="Text input" value={formContent.email} onChange={inputChange}/>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="columns">
                    <div className="column">
                        <div className="field">
                            <label className="label">Message</label>
                            <div className="control">
                                <textarea className="textarea" placeholder="" value={formContent.message} onChange={inputChange}/>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="columns is-centered">
                    <div className="column is-one-third">
                        <div className="field">
                            <div className="control">
                                <div className="button is-outlined is-medium is-fullwidth is-rounded">
                                    <input type="submit" value={"submit"}/>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </form>
        </div>
    </div>
}

export default ContactForm
