import React from "react";
import styled from "styled-components";

const IntroductionStyled = styled.div`
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: space-around;

  h1{
  margin: 10px;
  }
  p{
    line-height: 2;
  }
`

export default IntroductionStyled
